                        <div class="job-card-detail"><strong>Work Order:</strong> ${jobCard.work_order}</div>
                        <div class="job-card-detail"><strong>Operation:</strong> ${jobCard.operation}</div>
                        <div class="job-card-detail"><strong>Quantity:</strong> ${jobCard.for_quantity}</div>
