    bindEvents(frm, $wrapper);
    startTimerUpdates();
}

function formatTimer(startTime) {
    if (!startTime) return 'N/A';

    let start = new Date(startTime);
    let now = new Date();
    let diff = now - start;

    if (diff < 0) return 'N/A';

    let hours = Math.floor(diff / 3600000);
    let minutes = Math.floor((diff % 3600000) / 60000);
    let seconds = Math.floor((diff % 60000) / 1000);

    return `${hours}h ${minutes}m ${seconds}s`;
}

function startTimerUpdates() {
    setInterval(() => {
        $('.job-card-timer').each(function() {
            let startTime = $(this).data('start-time');
            let status = $(this).closest('.job-card').find('.job-card-status').text().trim();

            if (startTime && status !== "On Hold") {
                $(this).text(formatTimer(startTime));
            }
        });
    }, 1000);
}

