function startJob(frm, jobCard) {
    frappe.call({
        method: "erpnext.manufacturing.doctype.job_card.job_card.make_time_log",
        args: {
            args: {
                job_card_id: jobCard,
                start_time: frappe.datetime.now_datetime(),
                status: "Work In Progress"
            }
        },
        callback: (r) => {
            if (!r.exc) {
                frappe.show_alert(__('Job Card started'));
                refreshJobCards(frm);
            }
        }
    });
}

function pauseJob(frm, jobCard) {
    frappe.call({
        method: "erpnext.manufacturing.doctype.job_card.job_card.make_time_log",
        args: {
            args: {
                job_card_id: jobCard,
                complete_time: frappe.datetime.now_datetime(),
                status: "On Hold",
                completed_qty: 0
            }
        },
        callback: (r) => {
            if (!r.exc) {
                frappe.show_alert(__('Job Card paused'));
                refreshJobCards(frm);
                stopTimer(jobCard);
            }
        }
    });
}
function resumeJob(frm, jobCard) {
    frappe.call({
        method: "erpnext.manufacturing.doctype.job_card.job_card.make_time_log",
        args: {
            args: {
                job_card_id: jobCard,
                start_time: frappe.datetime.now_datetime(),
                status: "Work In Progress"
            }
        },
        callback: (r) => {
            if (!r.exc) {
                frappe.show_alert(__('Job Card resumed'));
                refreshJobCards(frm);
            }
        }
    });
}

function stopTimer(jobCard) {
    let timerInterval = $(`[data-name="${jobCard}"] .job-card-timer`).data('timer-interval');
    clearInterval(timerInterval);
}

function completeJob(frm, jobCard) {
    frappe.model.with_doc('Job Card', jobCard, function() {
        let doc = frappe.get_doc('Job Card', jobCard);
        let qtyToManufacture = doc.for_quantity;
        let completedQty = doc.total_completed_qty || 0;
        let remainingQty = qtyToManufacture - completedQty;
        
        frappe.prompt({
            fieldname: 'completed_qty',
            label: __('Completed Quantity'),
            fieldtype: 'Float',
            reqd: 1,
            default: remainingQty,
            description: `Quantity to manufacture: ${qtyToManufacture}, Already completed: ${completedQty}, Remaining: ${remainingQty}`
        }, function(values) {
            let newCompletedQty = values.completed_qty;
            
            if (newCompletedQty <= 0) {
                frappe.msgprint(__('Completed quantity must be greater than zero.'));
                return;
            }
            
            if (newCompletedQty > remainingQty) {
                frappe.msgprint(__('Completed quantity cannot exceed the remaining quantity.'));
                return;
            }
            
            frappe.call({
                method: "erpnext.manufacturing.doctype.job_card.job_card.make_time_log",
                args: {
                    args: {
                        job_card_id: jobCard,
                        complete_time: frappe.datetime.now_datetime(),
                        completed_qty: newCompletedQty
                    }
                },
                callback: function(r) {
                    if (!r.exc) {
                        frappe.model.with_doc('Job Card', jobCard, function() {
                            let updatedDoc = frappe.get_doc('Job Card', jobCard);
                            let totalCompletedQty = updatedDoc.total_completed_qty;
                            let status = totalCompletedQty >= qtyToManufacture ? 'Completed' : 'Work In Progress';
                            
                            frappe.db.set_value('Job Card', jobCard, 'status', status)
                                .then(() => {
                                    if (status === 'Completed') {
                                        return frappe.db.submit('Job Card', jobCard);
                                    }
                                })
                                .then(() => {
                                    frappe.show_alert(__(`Job Card ${status.toLowerCase()}`));
                                    refreshJobCards(frm);
                                    
                                    if (status !== 'Completed') {
                                        frappe.confirm(
                                            __('Do you want to create a new job card for the remaining quantity?'),
                                            () => createNewJobCard(updatedDoc, frm)
                                        );
                                    }
                                });
                        });
                    }
                }
            });
        }, __('Enter Completed Quantity'), __('Complete Job'));
    });
}

function createNewJobCard(parentDoc, frm) {
    frappe.model.with_doctype('Job Card', function() {
        let newJobCard = frappe.model.get_new_doc('Job Card');
        
        // Inherit fields from parent Job Card
        newJobCard.work_order = parentDoc.work_order;
        newJobCard.bom_no = parentDoc.bom_no;
        newJobCard.production_item = parentDoc.production_item;
        newJobCard.company = parentDoc.company;
        newJobCard.for_quantity = parentDoc.for_quantity - parentDoc.total_completed_qty;
        newJobCard.wip_warehouse = parentDoc.wip_warehouse;
        newJobCard.operation = parentDoc.operation;
        newJobCard.workstation = parentDoc.workstation;
        newJobCard.item_name = parentDoc.item_name;
        newJobCard.posting_date = frappe.datetime.get_today();
        newJobCard.operation_id = parentDoc.operation_id;
        newJobCard.sequence_id = parentDoc.sequence_id;

        // Set new Job Card specific fields
        newJobCard.status = 'Open';
        newJobCard.parent_job_card = parentDoc.name;
        newJobCard.naming_series = parentDoc.naming_series;

        // Handle time-related fields
        let now = frappe.datetime.now_datetime();
        newJobCard.planned_start_date = now;
        newJobCard.expected_start_date = now;
        
        // Calculate expected end date
        let expectedEndDate = new Date(now);
        expectedEndDate.setMinutes(expectedEndDate.getMinutes() + parentDoc.time_required);
        newJobCard.expected_end_date = frappe.datetime.get_datetime_as_string(expectedEndDate);

        // Inherit scheduled_time_logs if any
        if (parentDoc.scheduled_time_logs && parentDoc.scheduled_time_logs.length > 0) {
            newJobCard.scheduled_time_logs = [{
                from_time: newJobCard.expected_start_date,
                to_time: newJobCard.expected_end_date,
                time_in_mins: parentDoc.time_required
            }];
        }

        frappe.call({
            method: 'frappe.client.insert',
            args: {
                doc: newJobCard
            },
            callback: function(r) {
                if (!r.exc) {
                    frappe.show_alert(__('New Job Card created for remaining quantity'));
                    refreshJobCards(frm);
                } else {
                    frappe.msgprint(__('Error creating new Job Card: ') + r.exc);
                    console.error('Error details:', r);
                }
            }
        });
    });
}
