script:
frappe.ui.form.on('Workstation', {
    custom_generate_qr_code: function(frm) {
        frappe.call({
            method: 'aqiq_production_operations.aqiq_production_operations.rest.custom_workstation.generate_qr_code_button',
            args: {
                workstation_name: frm.doc.name
            },
            freeze: true,
            freeze_message: __('Generating QR Code...'),
            callback: function(r) {
                if (r.message) {
                    frm.reload_doc();
                    frappe.show_alert({
                        message: __('QR Code generated successfully'),
                        indicator: 'green'
                    });
                } else {
                    frappe.msgprint(__('Failed to generate QR Code. Please try again.'));
                }
            }
        });
    }
});
